import React from "react";
import Button from "@material-ui/core/Button";

export default class DynamicTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
    };
  }

  handleClick() {
    var items = this.state.items;

    items.push(this.state.message);

    this.setState({
      items: items,
    });
  }

  handleItemChanged(i, event) {
    var items = this.state.items;
    items[i] = event.target.value;

    this.setState({
      items: items,
    });
  }

  handleItemDeleted(i) {
    var items = this.state.items;

    items.splice(i, 1);

    this.setState({
      items: items,
    });
  }

  renderRows() {
    var context = this;

    return this.state.items.map(function (o, i) {
      return (
        <tr key={"item-" + i}>
          <td style={{ width: "25%" }}>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td style={{ width: "25%" }}>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td>
            <button onClick={context.handleItemDeleted.bind(context, i)}>
              Delete
            </button>
          </td>
        </tr>
      );
    });
  }

  render() {
    return (
      <div>
        <table className="">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Affiliation</th>
              <th>{/* delete button */}</th>
            </tr>
          </thead>
          <tbody>
            <tr key={"item-0"}>
              <td style={{ width: "25%" }}>
                <input
                  type="text"
                  value={"A"}
                  onChange={this.handleItemChanged.bind(this, 0)}
                />
              </td>
              <td style={{ width: "25%" }}>
                <input
                  type="text"
                  value={"B"}
                  onChange={this.handleItemChanged.bind(this, 0)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={"ABCDE"}
                  onChange={this.handleItemChanged.bind(this, 0)}
                />
              </td>
              <td>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={this.handleItemDeleted.bind(this, 0)}
                >
                  Delete
                </Button>
              </td>
            </tr>
            {this.renderRows()}
          </tbody>
        </table>
        <hr />
        <Button
          variant="contained"
          color="primary"
          onClick={this.handleClick.bind(this)}
        >
          Add Author
        </Button>
      </div>
    );
  }
}
