        {
          firstname: "Rosanna",
          lastname: "Migliore",
          affiliation:
            "Institute of Biophysics, National Research Council, Palermo, Italy",
        },
        {
          firstname: "Carmen A.",
          lastname: "Lupascu",
          affiliation:
            "Institute of Biophysics, National Research Council, Palermo, Italy",
        },
        {
