import React from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import Grid from "@material-ui/core/Grid";
import { withSnackbar } from "notistack";

import { withStyles } from "@material-ui/core/styles";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import DynamicTable from "./DynamicTable";
import SingleSelect from "./SingleSelect";

import nunjucks from "nunjucks";
import LivePaper from "./LivePaper.template";

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const footerStyle = {
  backgroundColor: "#ffd180",
  fontSize: "20px",
  color: "black",
  borderTop: "1px solid #000000",
  textAlign: "center",
  position: "fixed",
  left: "50%",
  transform: "translateX(-50%)",
  bottom: "0",
  height: "70px",
  width: "70%",
  zIndex: "1",
};

const phantomStyle = {
  display: "block",
  padding: "20px",
  height: "60px",
  width: "70%",
};

function Footer({ children }) {
  return (
    <div style={{ zIndex: "2147483638" }}>
      <div style={phantomStyle} />
      <div style={footerStyle}>{children}</div>
    </div>
  );
}

function showNotification(enqueueSnackbar, message, type = "default") {
  // type: default, success, error, warning, info
  enqueueSnackbar(message, {
    variant: type,
    anchorOrigin: {
      vertical: "bottom",
      horizontal: "right",
    },
  });
}

const MyDialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

class CreateLivePaper extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      popular_licenses: [
        "None",
        "Apache License 2.0",
        'BSD 2-Clause "Simplified" License',
        'BSD 3-Clause "New" or "Revised" License',
        "Creative Commons Attribution 4.0 International",
        "Creative Commons Attribution Non Commercial 4.0 International",
        "Creative Commons Attribution Share Alike 4.0 International",
        "Creative Commons Zero v1.0 Universal",
        "GNU General Public License v2.0 or later",
        "GNU General Public License v3.0 or later",
        "GNU Lesser General Public License v3.0 or later",
        "MIT License",
      ],
      authors: [{ firstname: "", lastname: "", affiliation: "" }],
      paper_title: "",
      journal: "",
      url: "",
      citation: "",
      doi: "",
      license: "None",
      abstract: "",
    };

    this.handleClose = this.handleClose.bind(this);
    this.handleDownloadLivePaper = this.handleDownloadLivePaper.bind(this);
    this.handlePreviewLivePaper = this.handlePreviewLivePaper.bind(this);
    this.handleSaveProject = this.handleSaveProject.bind(this);
    this.handleFieldChange = this.handleFieldChange.bind(this);
    this.handleAuthorsChange = this.handleAuthorsChange.bind(this);
  }

  handleClose() {
    this.props.onClose();
  }

  handleDownloadLivePaper() {
    // var nunjucks = require("nunjucks");
    function render(data) {
      fetch(LivePaper)
        .then((r) => r.text())
        .then((source) => {
          var output = nunjucks.renderString(source, data);

          const element = document.createElement("a");
          const file = new Blob([output], { type: "text/html" });
          element.href = URL.createObjectURL(file);
          element.download = "livepaper_demo.html";
          document.body.appendChild(element); // Required for this to work in FireFox
          element.click();
        });
    }
    var data = { paper_title: "Namaste" };
    render(data);

    showNotification(
      this.props.enqueueSnackbar,
      "Live Paper Downloaded...",
      "success"
    );
  }

  handlePreviewLivePaper() {
    function render(data) {
      fetch(LivePaper)
        .then((r) => r.text())
        .then((source) => {
          var output = nunjucks.renderString(source, data);
          var w = window.open("");
          w.document.write(output);
          w.document.close();
        });
    }
    var data = this.state;
    render(data);

    showNotification(
      this.props.enqueueSnackbar,
      "Preview generated...",
      "info"
    );
  }

  handleSaveProject() {
    showNotification(
      this.props.enqueueSnackbar,
      "Project Downloaded...",
      "success"
    );
  }

  handleFieldChange(event) {
    const target = event.target;
    let value = target.value;
    const name = target.name;
    console.log(name + " => " + value);
    this.setState({
      [name]: value,
    });
  }

  handleAuthorsChange(data) {
      this.setState({
          authors: data
      })
  }

  render() {
    // console.log(this.state.paper_title);
    return (
      <Dialog
        fullScreen
        onClose={this.handleClose}
        aria-labelledby="simple-dialog-title"
        open={true}
        // open={this.props.open}
      >
        <MyDialogTitle onClose={this.handleClose} />
        <DialogContent>
          <div className="container" style={{ textAlign: "left" }}>
            <div className="box rounded centered">
              <a
                href="../../index.html"
                className="waves-effect waves-light"
                style={{ textAlign: "center", color: "black" }}
              >
                <table>
                  <tbody>
                    <tr>
                      <td>
                        <img
                          className="hbp-icon-small"
                          src="./imgs/hbp_diamond_120.png"
                          alt="HBP logo"
                        />
                      </td>
                      <td>
                        <span
                          className="title-style subtitle"
                          style={{ paddingLeft: "5px" }}
                        >
                          EBRAINS Live Papers
                        </span>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </a>
              <h5 className="title-style">Live Paper Builder</h5>
            </div>
            <div
              style={{
                paddingLeft: "5%",
                paddingRight: "5%",
                textAlign: "justify",
              }}
            >
              <div>
                Please enter the information in the fields below. You can
                generate the live paper and/or save the project at any time by
                clicking on the buttons on the bottom of the page.
                <br />
                <br />
                When saving the project, a file with extension '.lpp' will be
                downloaded. You can upload this file later to update the live
                paper contents. Please do not manually edit these files, as it
                could render them unreadable by this tool.
              </div>
              <br />
              <div>
                <p>
                  <strong>
                    Enter the complete title of your manuscript / paper:
                  </strong>
                </p>
              </div>
              <div>
                <TextField
                  label="Title"
                  variant="outlined"
                  fullWidth={true}
                  name="paper_title"
                  onChange={this.handleFieldChange}
                  InputProps={{
                    style: {
                      padding: "0px 15px",
                    },
                  }}
                />
              </div>
              <br />
              <br />
              <div>
                <p>
                  <strong>
                    Enter details of all authors in the required order:
                  </strong>
                </p>
              </div>
              <div>
                <DynamicTable
                  value={this.state.authors}
                  onChangeValue={this.handleAuthorsChange}
                />
              </div>
              <br />
              <br />
              <div>
                <p>
                  <strong>
                    Specify the journal in which paper is published (leave empty
                    if awaiting publication):
                  </strong>
                </p>
              </div>
              <div>
                <TextField
                  label="Journal"
                  variant="outlined"
                  fullWidth={true}
                  name="journal"
                  onChange={this.handleFieldChange}
                  InputProps={{
                    style: {
                      padding: "0px 15px",
                    },
                  }}
                />
              </div>
              <div>
                <p>
                  <strong>
                    Provide the URL to access article (leave empty if awaiting
                    publication or link to bioRxiv, if relevant):
                  </strong>
                </p>
              </div>
              <div>
                <TextField
                  label="Article URL"
                  variant="outlined"
                  fullWidth={true}
                  name="url"
                  onChange={this.handleFieldChange}
                  InputProps={{
                    style: {
                      padding: "0px 15px",
                    },
                  }}
                />
              </div>
              <div>
                <p>
                  <strong>
                    Specify the citation text to be used for article (leave
                    empty if awaiting publication):
                  </strong>
                </p>
              </div>
              <div>
                <TextField
                  multiline
                  rows="3"
                  label="Citation"
                  variant="outlined"
                  fullWidth={true}
                  name="citation"
                  onChange={this.handleFieldChange}
                  InputProps={{
                    style: {
                      padding: "15px 15px",
                    },
                  }}
                />
              </div>
              <div>
                <p>
                  <strong>
                    Indicate the DOI entry for article (leave empty if awaiting
                    publication):
                  </strong>
                </p>
              </div>
              <div>
                <TextField
                  label="DOI"
                  variant="outlined"
                  fullWidth={true}
                  name="doi"
                  onChange={this.handleFieldChange}
                  InputProps={{
                    style: {
                      padding: "0px 15px",
                    },
                  }}
                />
              </div>
              <br />
              <div>
                <p>
                  <strong>
                    Provide the abstract of your manuscript / paper:
                  </strong>
                </p>
              </div>
              <div>
                <Grid item xs={12}>
                  <TextField
                    multiline
                    rows="8"
                    label="Abstract"
                    // defaultValue={this.state.description}
                    // onBlur={this.handleFieldChange}
                    variant="outlined"
                    fullWidth={true}
                    helperText="The description may be formatted with Markdown"
                    name="abstract"
                    onChange={this.handleFieldChange}
                    InputProps={{
                      style: {
                        padding: "15px 15px",
                      },
                    }}
                  />
                </Grid>
              </div>
              <br />
              <br />
              <div>
                <p>
                  <strong>
                    Specify the license to be applied for the contents displayed
                    in this live paper:
                  </strong>
                </p>
              </div>
              <div>
                <SingleSelect
                  itemNames={this.state.popular_licenses}
                  label="license"
                  value={this.state.license}
                  handleChange={this.handleFieldChange}
                  helperText="For guidance on choosing a licence, see https://choosealicense.com"
                />
              </div>

              <br />
              <br />

              <h5>Resources</h5>
              <p>Add a line of description:</p>
              <ul className="collapsible" data-collapsible="expandable">
                <li className="active">
                  <div className="collapsible-header amber lighten-5">
                    <i className="material-icons">settings_input_antenna</i>Add
                    heading
                  </div>
                  <div className="collapsible-body ">Add content here</div>
                </li>
                <li>
                  <div className="collapsible-header amber lighten-5">
                    <i className="material-icons">timeline</i>Add heading
                  </div>
                  <div className="collapsible-body">Add content here</div>
                </li>
                <li>
                  <div className="collapsible-header amber lighten-5">
                    <i className="material-icons">note_add</i>Add heading
                  </div>
                  <div className="collapsible-body">Add content here</div>
                </li>
                <li>
                  <div className="collapsible-header amber lighten-5">
                    <i className="material-icons">local_play</i>Add heading
                  </div>
                  <div className="collapsible-body">Add content here</div>
                </li>
              </ul>
              <br />
              <br />
            </div>
          </div>
          <Footer>
            <div className="rainbow-row">
              <div></div>
              <div></div>
              <div></div>
              <div></div>
              <div></div>
              <div></div>
              <div></div>
              <div></div>
            </div>
            <div
              style={{
                display: "flex",
                justifyContent: "space-around",
                alignItems: "center",
              }}
            >
              <Button
                variant="contained"
                color="primary"
                style={{
                  width: "25%",
                  backgroundColor: "#FF9800",
                  color: "#000000",
                  fontWeight: "bold",
                }}
                onClick={this.handlePreviewLivePaper}
              >
                Preview Live Paper
              </Button>
              <br />
              <br />
              <Button
                variant="contained"
                color="primary"
                style={{
                  width: "25%",
                  backgroundColor: "#009688",
                  color: "#000000",
                  fontWeight: "bold",
                }}
                onClick={this.handleDownloadLivePaper}
              >
                Download Live Paper
              </Button>
              <br />
              <br />
              <Button
                variant="contained"
                color="secondary"
                style={{
                  width: "25%",
                  backgroundColor: "#01579b",
                  fontWeight: "bold",
                }}
                onClick={this.handleSaveProject}
              >
                Save & Download Project
              </Button>
            </div>
          </Footer>
        </DialogContent>
      </Dialog>
    );
  }
}
export default withSnackbar(CreateLivePaper);
