import React from "react";
import Button from "@material-ui/core/Button";

export default class DynamicTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [{ firstname: "", lastname: "", affiliation: "" }],
    };
  }

  handleClick() {
    var items = this.state.items;

    items.push(this.state.message);

    this.setState({
      items: items,
    });
  }

  handleItemChanged(i, event) {
    var items = this.state.items;
    items[i] = event.target.value;

    this.setState({
      items: items,
    });
  }

  handleItemDeleted(i) {
    var items = this.state.items;

    items.splice(i, 1);

    this.setState({
      items: items,
    });
  }

  renderRows() {
    var context = this;

    return this.state.items.map(function (o, i) {
      return (
        <tr key={"item-" + i}>
          <td style={{ width: "25%" }}>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td style={{ width: "25%" }}>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td>
            <input
              type="text"
              value={o}
              onChange={context.handleItemChanged.bind(context, i)}
            />
          </td>
          <td>
            <Button
              variant="contained"
              color="secondary"
              onClick={context.handleItemDeleted.bind(context, i)}
            >
              Delete
            </Button>
          </td>
        </tr>
      );
    });
  }

  render() {
    console.log(this.state.items);
    return (
      <div>
        <table className="">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Affiliation</th>
              <th>{/* delete button */}</th>
            </tr>
          </thead>
          <tbody>
            {this.renderRows()}
          </tbody>
        </table>
        <hr />
        <Button
          variant="contained"
          color="primary"
          onClick={this.handleClick.bind(this)}
        >
          Add Author
        </Button>
      </div>
    );
  }
}
